import React from 'react';
import Productos from './componentes/Productos';
import './App.css';
import Navbar from './componentes/Navbar';
import Products from './componentes/Products';
import CheckoutPage from './componentes/CheckoutPage';
import CheckoutCard from './componentes/CheckoutCard';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link
} from "react-router-dom";
import SignIn from './componentes/Signin';
import SignUp from './componentes/Signup';
import {useState, useEffect} from 'react';
import { auth } from './firebase';
import { db } from './firebase';
import { collection, getDocs } from 'firebase/firestore';
import { actionTypes } from './reducer';
import { useStateValue } from './StateProvider';
import Checkout from './componentes/CheckoutForm/Checkout';


function App() {
  const [{user}, dispatch] = useStateValue();
  useEffect(()=>{
    auth.onAuthStateChanged((authUser)=>{
      console.log(authUser);
      if(authUser){
        dispatch({
          type: actionTypes.SET_USER,
          user: authUser,
        })
      }
    })
  },[])

  const citiesRef = collection(db, "usuarioss");
  let nom = false;


  getDocs(citiesRef)
      .then((snapshot) => {
        //console.log(snapshot.docs);
        //nom = ""
        let nomb = []
        snapshot.docs.forEach((doc) => {
          nomb = {...doc.data() };
          if(nomb.correo == user.email){

            nom = nomb.ap;
            console.log("nomb", nom);
          }
        })
      })
      .catch(err => {
        console.log(err.message);
      })


  return (
    <Router>
          <div className="App">
     <Navbar/>
     {/*<CheckoutPage/>*/}

     <Routes>
        if (nom.ad) {<Route path="/signup" element={<SignUp/>}/>}
        <Route path="/signup" element={<SignUp/>}/>
        <Route path="/signin" element={<SignIn/>}/>
        <Route path="/checkout-page" element={<CheckoutPage/>}/>
        <Route path="/checkout" element={<Checkout/>}/>
        <Route path="/" element={<Products/>} />
     </Routes>    
    </div>

    </Router>

  );
}

export default App;
