import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Productos from './Productos';
import products from '../product-data'; //llamar a la base de datos
import { db } from '../firebase';
import { collection, getDocs } from 'firebase/firestore';
import {useState, useEffect} from 'react';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    padding: theme.spacing(2),
  },
  
}));

export default function Products() {
  const classes = useStyles();

  const [productos, setProductos] = useState();
  const productoColleccion = collection(db, "productos");
  useEffect(() => {
    const getProductos = async () => {
      const prod = await getDocs(productoColleccion);
      //setProductos(db.docs.map((doc) => ({...doc.data(), id: doc.id})))
      console.log(prod);
    }

    //getProductos()

  }, [])

  return (
    <div className={classes.root}>
      <Grid container spacing={2}>
          {
              productos.map(producto =>(
                <div item xs={12} sm={6} md={4} lg={3}>
                    <Productos key={producto.id} producto={producto}/>
                </div>
              ))
          }
      </Grid>
    </div>
  );
}
