import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Productos from './Productos';
import products from '../product-data';
import { db } from '../firebase';
import { collection, getDocs, orderBy, query } from 'firebase/firestore';
import {useState, useEffect} from 'react';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    padding: theme.spacing(2),
  },
  
}));

export default function Products() {
  const classes = useStyles();

  const [productos, setProductos] = useState([]);
  const productoColleccion = collection(db, "productos");
  useEffect(() => {
    const getProductos = async () => {
      const prod = await getDocs(productoColleccion);
      setProductos(prod.docs.map((doc) => ({...doc.data(), id: doc.id})));
    }
    getProductos();
  }, [])

  const ProductsValor = () => {
    
      const getProductos = async () => {
        //const prod = await getDocs(productoColleccion);
        const q = query(productoColleccion, orderBy('price', 'asc'));
        console.log("que", q.doc)
        getDocs(q)
          
          //console.log(snapshot.docs);
          //nom = ""
          //setProductos([])
          //let nomb = []
          //setProductos(q.docs.map((doc) => ({...doc.data(), id: doc.id})));
          
          .then((snapshot) => {
            //console.log(snapshot.docs);
            //nom = ""
            //nomb = []
            setProductos([])
            snapshot.docs.forEach((doc) => {
              setProductos(prod.docs.map((doc) => ({...doc.data(), id: doc.id})))
            })
          })
          .catch(err => {
            console.log(err.message);
          })
        //setProductos([])
        //setProductos(q.docs.map((doc) => ({...doc.data(), id: doc.id})));  
      } 
      getProductos();
  }

  return (
    <div className={classes.root}>
      <button onClick={ProductsValor}> Ordenar por precio</button>
      
      <Grid container spacing={2}>
          {
              productos.map(producto => (
                <div item xs={12} sm={6} md={4} lg={3}>
                    <Productos producto={producto} key={producto.id}/>
                </div>
              ))
          }
      </Grid>
    </div>
  );
}
